import { useAppDispatch, useAppSelector } from '../store/hoocs';
import { checkIsAuth, getMe, logout } from '../store/reducers/authSlice';
import '../styles/Dashboard.css';
import {useEffect, useState, useRef} from 'react'
import { useNavigate } from 'react-router-dom'

const Dashboard = () => {
    const navigate = useNavigate();
    const dispatch = useAppDispatch();
    const { isLoading, status } = useAppSelector((state) => state.authSlice);
    const user = useAppSelector((state) => state.authSlice.user);


    const [daysSinceRegistration, setDaysSinceRegistration] = useState<number | null>(null);
    const [message, setMessage] = useState<string | null>('')
    
    const isAuth = useAppSelector((state) => checkIsAuth(state.authSlice));
    useEffect(() => {
        if (!isAuth) {
            navigate('/login')
        }
    },[])

    useEffect(() => {
        if (status) {
          setMessage(status);
    
          const clearMessage = setInterval(() => {
            setMessage('');
          }, 3000);
          return () => {
            clearInterval(clearMessage);
          };
        }
    
      }, [status, navigate]);

  useEffect(() => {
    dispatch(getMe());

    if (user && user.createdAt) {
      const createdAtDate = new Date(user.createdAt);
      const currentDate = new Date();

      const timeDifference = Math.abs(currentDate.getTime() - createdAtDate.getTime());
      const daysDifference = Math.floor(timeDifference / (1000 * 60 * 60 * 24));

      setDaysSinceRegistration(daysDifference);
    }
  }, [dispatch, user?.createdAt]);

  const handleLogout = () => {
    dispatch(logout());
    window.localStorage.removeItem('token');
    navigate('/login');
  }

  const userNickName = user?.username.split(' ').map((a) => a[0]+a[1]).join('.')

    return (
        <div className='dashboard'>
            <div className='user_avatar'>
                <h4>{userNickName}</h4>
            </div>
            <h2>{user?.username}</h2>
            <h2>{user?.email}</h2>

            <div>
              
            </div>
            <button onClick={handleLogout}>Выйти</button>
        </div>
    )
}

export default Dashboard
