import React, {useState, useEffect} from 'react';
import '../styles/register.css';
import { useAppDispatch, useAppSelector } from '../store/hoocs';
import { registerUser } from '../store/reducers/authSlice';
import { useNavigate } from 'react-router-dom';

const Register = () => {
    const [password, setPassword] = useState<string>('')
    const [email, setEmail] = useState<string>('');
    const [username, setUsername] = useState<string>('');

    const dispatch = useAppDispatch();
    const navigate = useNavigate();

    const [msg, setMsg] = useState<string | null>('');
    const { status, isLoading } = useAppSelector((state) => state.authSlice);
    useEffect(() => {
        if (status) {
          setMsg(status)
    
          const clearMessage = setInterval(() => {setMsg('')}, 3000)
          return () => { clearInterval(clearMessage)}
        }
      },[status])

    const registration = () => {
        try {
            dispatch(registerUser({username, email, password}))
            setEmail('');
            setUsername('');
            setPassword('');
        } catch (e) {
            console.log(e)
        }
    }

    return (
        <div className='register'>
            <h3>Register Page</h3>
                <form onSubmit={(e) => e.preventDefault()} className='register_container'>
                    <div> 
                        <h2>Username</h2>
                        <input 
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        placeholder='Your username...'/>
                    </div>

                    <div>
                        <h2>E-mail</h2>
                        <input 
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder='Your email...'/>
                    </div>

                    <div>
                        <h2>Password</h2>
                        <input 
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder='Your password...'/>
                    </div>

                    <button onClick={registration}>Регистрация</button>
                </form>

                <div className='button_section'>
                    <span>Уже есть аккаунт?</span><a href='https://www.google.com'>Войти</a>
                </div>
            <div>{msg ? <h2>{msg}</h2> : ''}</div>
        </div>
    )
}

export default Register
